//  Helpers 
function uid(){return Date.now().toString(36)+Math.random().toString(36).slice(2,8)}
function getWeekNumber(d){const date=new Date(Date.UTC(d.getFullYear(),d.getMonth(),d.getDate()));date.setUTCDate(date.getUTCDate()+4-(date.getUTCDay()||7));const ys=new Date(Date.UTC(date.getUTCFullYear(),0,1));return Math.ceil(((date-ys)/86400000+1)/7)}
function getMondayOfWeek(wo=0){const n=new Date();const d=n.getDay();const m=new Date(n);m.setDate(n.getDate()-(d===0?6:d-1)+wo*7);m.setHours(0,0,0,0);return m}
function fmtFull(d){const date=new Date(d);const days=["Sndag","Mandag","Tirsdag","Onsdag","Torsdag","Fredag","Lrdag"];return`${days[date.getDay()]} ${date.getDate()}.${(date.getMonth()+1).toString().padStart(2,"0")}`}
function isSameWeek(ds,mon){const d=new Date(ds);const sun=new Date(mon);sun.setDate(sun.getDate()+6);sun.setHours(23,59,59,999);return d>=mon&&d<=sun}
function isPast(ds,time){const d=new Date(ds);const[h,m]=time.split(":").map(Number);d.setHours(h+1,m,0,0);return new Date()>d}
function defaultState(){return{admins:[{username:"admin",password:"njord2025"}],sessions:[],teamsWebhook:"",maxSpots:MAX_SPOTS}}

function useStorage(){
  const[data,setData]=useState(null);
  useEffect(()=>{(async()=>{try{const r=await window.storage.get(APP_STORAGE);if(r?.value)setData({...defaultState(),...JSON.parse(r.value)});else setData(defaultState())}catch{setData(defaultState())}})()},[]);
  const save=useCallback(async nd=>{setData(nd);try{await window.storage.set(APP_STORAGE,JSON.stringify(nd))}catch{}},[]);
  return[data,save];
}
async function notifyTeams(url,msg){if(!url)return;try{await fetch(url,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({"@type":"MessageCard",summary:"Spinning",themeColor:"0078D4",title:" Spinning Njord A",text:msg})})}catch{}}

//  Theme Context 
const ThemeCtx = createContext();
function useTheme(){return useContext(ThemeCtx)}

//  Animated Bike Wheel SVG 
function BikeWheel({size=48, strokeColor="#CBD5E1", accent="#3B82F6", speed="3s"}) {
  return (
    <svg width={size} height={size} viewBox="0 0 60 60" style={{animation:`spin ${speed} linear infinite`}}>
      <circle cx="30" cy="30" r="26" fill="none" stroke={strokeColor} strokeWidth="2.5"/>
      <circle cx="30" cy="30" r="4" fill={accent}/>
      {[0,45,90,135,180,225,270,315].map(a=>(
        <line key={a} x1="30" y1="30" x2={30+22*Math.cos(a*Math.PI/180)} y2={30+22*Math.sin(a*Math.PI/180)} stroke={strokeColor} strokeWidth="1" opacity="0.6"/>
      ))}
      <circle cx="30" cy="30" r="26" fill="none" stroke={accent} strokeWidth="2.5" strokeDasharray="8 155" strokeLinecap="round"/>
    </svg>
  );
}

