//  Badge 
function Badge({children,color="gray"}) {
  const t=useTheme();
  const map={gray:t.badgeGray,blue:t.badgeBlue,red:t.badgeRed,yellow:t.badgeYellow};
  return <span className={`text-xs font-semibold px-2.5 py-1 rounded-full ${map[color]}`}>{children}</span>;
}

//  Input 
function Input({label,...props}){
  const t=useTheme();
  return(<div>{label&&<label className="block text-sm font-medium text-gray-500 mb-1.5">{label}</label>}<input {...props} className={`w-full rounded-xl px-4 py-2.5 text-gray-800 placeholder-gray-400 focus:outline-none focus:ring-2 transition-all text-sm ${t.input}`}/></div>);
}

//  Button 
function Button({children,onClick,disabled,variant="primary",size="md"}){
  const t=useTheme();
  const base="font-semibold rounded-xl transition-all duration-150 ";
  const sizes={sm:"px-3 py-1.5 text-sm",md:"px-4 py-2.5 text-sm"};
  const v={
    primary:disabled?"bg-gray-100 text-gray-400 cursor-not-allowed":`${t.btnPrimary} shadow-sm hover:shadow active:scale-95`,
    ghost:disabled?"text-gray-300 cursor-not-allowed":`${t.btnGhost} active:scale-95`,
  };
  return <button onClick={onClick} disabled={disabled} className={`${base} ${sizes[size]} ${v[variant]}`}>{children}</button>;
}

//  Session Card 
function SessionCard({session,userName,onSignup,onLeave,maxSpots,isAdmin,onEdit,onCancel,onRestore,onDelete}){
  const t=useTheme();
  const past=isPast(session.date,session.time);
  const cancelled=session.status==="cancelled";
  const list=session.signups||[];
  const waitlist=session.waitlist||[];
  const full=list.length>=maxSpots;
  const nameLC=userName?.trim().toLowerCase();
  const isSignedUp=nameLC&&list.some(n=>n.toLowerCase()===nameLC);
  const isOnWaitlist=nameLC&&waitlist.some(n=>n.toLowerCase()===nameLC);
  const waitPos=isOnWaitlist?waitlist.findIndex(n=>n.toLowerCase()===nameLC)+1:0;
  const fillPct=Math.min((list.length/maxSpots)*100,100);

  const borderClass=cancelled?t.cardCancelled:past?t.cardPast:t.card;
  return(
    <div className={`rounded-2xl overflow-hidden border-2 transition-all ${borderClass} ${cancelled||past?"opacity-50":""}`}>
      <div className={`${t.cardHead} px-5 py-4 flex items-center justify-between`}>
        <div>
          <div className="font-bold text-gray-800">{fmtFull(session.date)}</div>
          <div className="text-sm text-gray-400 mt-0.5">kl. {session.time}{session.label&&session.label!=="Spinning"?`  ${session.label}`:""}</div>
        </div>
        <div className="flex items-center gap-2">
          {cancelled?<Badge color="red">Avlyst</Badge>:(
            <><Badge color={full?"red":list.length>0?"blue":"gray"}>{list.length}/{maxSpots}</Badge>{waitlist.length>0&&<Badge color="yellow">{waitlist.length} venter</Badge>}</>
          )}
          {isAdmin&&!past&&(
            <div className="flex gap-0.5 ml-1">
              {!cancelled&&<button onClick={()=>onEdit(session)} className="text-gray-400 hover:text-blue-500 text-xs p-1.5 rounded-lg hover:bg-blue-50 transition-colors"></button>}
              {cancelled?(<><button onClick={()=>onRestore(session.id)} className="text-gray-400 hover:text-green-500 text-xs p-1.5 rounded-lg hover:bg-green-50 transition-colors"></button><button onClick={()=>onDelete(session.id)} className="text-gray-400 hover:text-red-500 text-xs p-1.5 rounded-lg hover:bg-red-50 transition-colors"></button></>):(<button onClick={()=>onCancel(session.id)} className="text-gray-400 hover:text-red-500 text-xs p-1.5 rounded-lg hover:bg-red-50 transition-colors"></button>)}
            </div>
          )}
        </div>
      </div>
      {!cancelled&&list.length>0&&(
        <div className="px-5 pb-2 bg-white"><div className="h-1.5 bg-gray-100 rounded-full overflow-hidden"><div className="h-full rounded-full transition-all duration-500" style={{width:`${fillPct}%`,background:full?t.progressFull:fillPct>70?t.progressMid:t.progressLow}}/></div></div>
      )}
      {cancelled&&<div className={`px-5 py-3 ${t.cancelled} text-sm font-medium`}>Denne kten er avlyst</div>}
      {!cancelled&&(
        <>
          <div className="bg-gray-50 bg-opacity-50">
            {list.length===0&&waitlist.length===0?(<div className="px-5 py-4 text-gray-400 text-sm italic">Ingen pmeldte enn</div>):(
              <>
                {list.map((p,i)=>(
                  <div key={i} className="px-5 py-2.5 flex items-center justify-between border-t border-gray-100">
                    <div className="flex items-center gap-3"><span className="text-xs font-bold text-gray-300 w-5 text-right">{i+1}</span><span className="text-gray-700 font-medium">{p}</span></div>
                    {!past&&p.toLowerCase()===nameLC&&<button onClick={()=>onLeave(session.id,p,"signup")} className="text-gray-400 hover:text-red-500 text-xs font-medium transition-colors px-2 py-1 rounded hover:bg-red-50">Meld av</button>}
                    {!past&&isAdmin&&p.toLowerCase()!==nameLC&&<button onClick={()=>onLeave(session.id,p,"signup")} className="text-gray-300 hover:text-red-400 text-xs transition-colors p-1 rounded hover:bg-red-50"></button>}
                  </div>
                ))}
                {waitlist.length>0&&(<>
                  <div className="px-5 py-2 border-t border-gray-100"><span className="text-xs text-amber-500 font-bold uppercase tracking-wider">Venteliste</span></div>
                  {waitlist.map((p,i)=>(
                    <div key={i} className="px-5 py-2.5 flex items-center justify-between border-t border-gray-100">
                      <div className="flex items-center gap-3"><span className="text-xs font-bold text-amber-300 w-5 text-right">{i+1}</span><span className="text-gray-500">{p}</span></div>
                      {!past&&p.toLowerCase()===nameLC&&<button onClick={()=>onLeave(session.id,p,"waitlist")} className="text-gray-400 hover:text-red-500 text-xs font-medium transition-colors px-2 py-1 rounded hover:bg-red-50">Trekk deg</button>}
                    </div>
                  ))}
                </>)}
              </>
            )}
          </div>
          {!past&&(
            <div className="border-t border-gray-100">
              {isSignedUp?(<div className={`py-3 text-center text-sm font-semibold ${t.signedUp}`}> Du er pmeldt</div>
              ):isOnWaitlist?(<div className={`py-3 text-center text-sm font-semibold ${t.waitlisted}`}>Venteliste  plass {waitPos}</div>
              ):(<button onClick={()=>onSignup(session.id)} disabled={!nameLC}
                className={`w-full py-3.5 text-sm font-bold transition-all ${!nameLC?"bg-gray-50 text-gray-300 cursor-not-allowed":full?`${t.accentWait} text-white`:`${t.accent} text-white`}`}>
                {full?"Sett meg p venteliste":"Meld p"}
              </button>)}
            </div>
          )}
        </>
      )}
    </div>
  );
}

//  Session Modal 
function SessionModal({session,monday,onSave,onClose}){
  const t=useTheme();
  const gdd=()=>{const d=new Date(monday);const td=new Date();if(td>monday)d.setDate(d.getDate()+Math.min(Math.ceil((td-monday)/86400000),6));return d.toISOString().split("T")[0]};
  const[date,setDate]=useState(session?.date||gdd());
  const[time,setTime]=useState(session?.time||"19:40");
  const[label,setLabel]=useState(session?.label||"Spinning");
  const isEdit=!!session?.id;
  const dn=["Man","Tir","Ons","Tor","Fre","Lr","Sn"];
  const wd=Array.from({length:7},(_,i)=>{const d=new Date(monday);d.setDate(d.getDate()+i);return d});
  return(
    <div className="fixed inset-0 bg-black bg-opacity-30 backdrop-blur-sm flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-2xl p-6 w-full max-w-sm shadow-2xl border border-gray-100">
        <h3 className="text-lg font-bold text-gray-800 mb-4">{isEdit?"Rediger kt":"Ny kt"}</h3>
        <label className="block text-sm font-medium text-gray-500 mb-2">Dag</label>
        <div className="grid grid-cols-7 gap-1.5 mb-4">
          {wd.map((w,i)=>{const ds=w.toISOString().split("T")[0];const sel=date===ds;const dp=w<new Date(new Date().setHours(0,0,0,0));
            return(<button key={i} onClick={()=>!dp&&setDate(ds)} disabled={dp} className={`py-2 rounded-xl text-center transition-all ${dp?"text-gray-300 cursor-not-allowed":sel?`${t.btnPrimary} shadow-md scale-105`:"bg-gray-100 text-gray-600 hover:bg-gray-200"}`}>
              <div className="text-xs font-medium">{dn[i]}</div><div className="text-sm font-bold">{w.getDate()}</div>
            </button>)})}
        </div>
        <div className="space-y-3">
          <Input label="Klokkeslett" type="time" value={time} onChange={e=>setTime(e.target.value)}/>
          <Input label="Beskrivelse" type="text" value={label} onChange={e=>setLabel(e.target.value)} placeholder="Spinning, Intervall, Rolig..."/>
        </div>
        <div className="flex gap-2 mt-5"><Button variant="ghost" onClick={onClose}>Avbryt</Button><Button onClick={()=>onSave({...session,date,time,label})} disabled={!date||!time}>{isEdit?"Lagre":"Opprett"}</Button></div>
      </div>
    </div>
  );
}

//  Login 
function LoginModal({admins,onLogin,onClose}){
  const[user,setUser]=useState("");const[pass,setPass]=useState("");const[error,setError]=useState(false);
  const tryLogin=()=>{const m=admins.find(a=>a.username.toLowerCase()===user.toLowerCase()&&a.password===pass);if(m)onLogin(m.username);else{setError(true);setTimeout(()=>setError(false),2000)}};
  return(
    <div className="fixed inset-0 bg-black bg-opacity-30 backdrop-blur-sm flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-2xl p-6 w-full max-w-sm shadow-2xl border border-gray-100">
        <h3 className="text-lg font-bold text-gray-800 mb-4"> Admin</h3>
        <div className="space-y-3">
          <Input placeholder="Brukernavn" value={user} onChange={e=>setUser(e.target.value)} onKeyDown={e=>e.key==="Enter"&&tryLogin()}/>
          <Input placeholder="Passord" type="password" value={pass} onChange={e=>setPass(e.target.value)} onKeyDown={e=>e.key==="Enter"&&tryLogin()}/>
          {error&&<p className="text-red-500 text-sm font-medium">Feil brukernavn eller passord</p>}
        </div>
        <div className="flex gap-2 mt-5"><Button variant="ghost" onClick={onClose}>Avbryt</Button><Button onClick={tryLogin}>Logg inn</Button></div>
      </div>
    </div>
  );
}

